type debtScope struct {
	name  string
	apply func(builder query.Condition)
}

var debtGlobalScopes = make([]debtScope, 0)
var debtLocalScopes = make([]debtScope, 0)

// AddGlobalScopeForDebt assign a global scope to a model
func AddGlobalScopeForDebt(name string, apply func(builder query.Condition)) {
	debtGlobalScopes = append(debtGlobalScopes, debtScope{name: name, apply: apply})
}

// AddLocalScopeForDebt assign a local scope to a model
func AddLocalScopeForDebt(name string, apply func(builder query.Condition)) {
	debtLocalScopes = append(debtLocalScopes, debtScope{name: name, apply: apply})
}

func (m *DebtModel) applyScope() query.Condition {
	scopeCond := query.ConditionBuilder()
	for _, g := range debtGlobalScopes {
		if m.globalScopeEnabled(g.name) {
			g.apply(scopeCond)
		}
	}

	for _, s := range debtLocalScopes {
		if m.localScopeEnabled(s.name) {
			s.apply(scopeCond)
		}
	}

	return scopeCond
}

func (m *DebtModel) localScopeEnabled(name string) bool {
	for _, n := range m.includeLocalScopes {
		if name == n {
			return true
		}
	}

	return false
}

func (m *DebtModel) globalScopeEnabled(name string) bool {
	for _, n := range m.excludeGlobalScopes {
		if name == n {
			return false
		}
	}

	return true
}

type Debt struct {
	Id        int64 `json:"id"`
	UserId    int64 `json:"user_id"`
	Used      int64 `json:"used"`
	CreatedAt time.Time
	UpdatedAt time.Time
}

func (w Debt) ToDebtN(allows ...string) DebtN {
	if len(allows) == 0 {
		return DebtN{

			Id:        null.IntFrom(int64(w.Id)),
			UserId:    null.IntFrom(int64(w.UserId)),
			Used:      null.IntFrom(int64(w.Used)),
			CreatedAt: null.TimeFrom(w.CreatedAt),
			UpdatedAt: null.TimeFrom(w.UpdatedAt),
		}
	}

	res := DebtN{}
	for _, al := range allows {
		switch strcase.ToSnake(al) {

		case "id":
			res.Id = null.IntFrom(int64(w.Id))
		case "user_id":
			res.UserId = null.IntFrom(int64(w.UserId))
		case "used":
			res.Used = null.IntFrom(int64(w.Used))
		case "created_at":
			res.CreatedAt = null.TimeFrom(w.CreatedAt)
		case "updated_at":
			res.UpdatedAt = null.TimeFrom(w.UpdatedAt)
		default:
		}
	}

	return res
}

