// Count return model count for a given query
func (m *DebtModel) Count(ctx context.Context, builders ...query.SQLBuilder) (int64, error) {
	sqlStr, params := m.query.
		Merge(builders...).
		Table(m.tableName).
		AppendCondition(m.applyScope()).
		ResolveCount()

	rows, err := m.db.QueryContext(ctx, sqlStr, params...)
	if err != nil {
		return 0, err
	}

	defer rows.Close()

	rows.Next()
	var res int64
	if err := rows.Scan(&res); err != nil {
		return 0, err
	}

	return res, nil
}

func (m *DebtModel) Paginate(ctx context.Context, page int64, perPage int64, builders ...query.SQLBuilder) ([]DebtN, query.PaginateMeta, error) {
	if page <= 0 {
		page = 1
	}

	if perPage <= 0 {
		perPage = 15
	}

	meta := query.PaginateMeta{
		PerPage: perPage,
		Page:    page,
	}

	count, err := m.Count(ctx, builders...)
	if err != nil {
		return nil, meta, err
	}

	meta.Total = count
	meta.LastPage = count / perPage
	if count%perPage != 0 {
		meta.LastPage += 1
	}

	res, err := m.Get(ctx, append([]query.SQLBuilder{query.Builder().Limit(perPage).Offset((page - 1) * perPage)}, builders...)...)
	if err != nil {
		return res, meta, err
	}

	return res, meta, nil
}

// Get retrieve all results for given query
func (m *DebtModel) Get(ctx context.Context, builders ...query.SQLBuilder) ([]DebtN, error) {
	b := m.query.Merge(builders...).Table(m.tableName).AppendCondition(m.applyScope())
	if len(b.GetFields()) == 0 {
		b = b.Select(
			"id",
			"user_id",
			"used",
			"created_at",
			"updated_at",
		)
	}

	fields := b.GetFields()
	selectFields := make([]query.Expr, 0)

	for _, f := range fields {
		switch strcase.ToSnake(f.Value) {

		case "id":
			selectFields = append(selectFields, f)
		case "user_id":
			selectFields = append(selectFields, f)
		case "used":
			selectFields = append(selectFields, f)
		case "created_at":
			selectFields = append(selectFields, f)
		case "updated_at":
			selectFields = append(selectFields, f)
		}
	}

	var createScanVar = func(fields []query.Expr) (*DebtN, []interface{}) {
		var debtVar DebtN
		scanFields := make([]interface{}, 0)

		for _, f := range fields {
			switch strcase.ToSnake(f.Value) {

			case "id":
				scanFields = append(scanFields, &debtVar.Id)
			case "user_id":
				scanFields = append(scanFields, &debtVar.UserId)
			case "used":
				scanFields = append(scanFields, &debtVar.Used)
			case "created_at":
				scanFields = append(scanFields, &debtVar.CreatedAt)
			case "updated_at":
				scanFields = append(scanFields, &debtVar.UpdatedAt)
			}
		}

		return &debtVar, scanFields
	}

	sqlStr, params := b.Fields(selectFields...).ResolveQuery()

	rows, err := m.db.QueryContext(ctx, sqlStr, params...)
	if err != nil {
		return nil, err
	}

	defer rows.Close()

	debts := make([]DebtN, 0)
	for rows.Next() {
		debtReal, scanFields := createScanVar(fields)
		if err := rows.Scan(scanFields...); err != nil {
			return nil, err
		}

		debtReal.original = &debtOriginal{}
		_ = query.Copy(debtReal, debtReal.original)

		debtReal.SetModel(m)
		debts = append(debts, *debtReal)
	}

	return debts, nil
}

// First return first result for given query
func (m *DebtModel) First(ctx context.Context, builders ...query.SQLBuilder) (*DebtN, error) {
	res, err := m.Get(ctx, append(builders, query.Builder().Limit(1))...)
	if err != nil {
		return nil, err
	}

	if len(res) == 0 {
		return nil, query.ErrNoResult
	}

	return &res[0], nil
}

// Create save a new debt to database
func (m *DebtModel) Create(ctx context.Context, kv query.KV) (int64, error) {

	if _, ok := kv["created_at"]; !ok {
		kv["created_at"] = time.Now()
	}

	if _, ok := kv["updated_at"]; !ok {
		kv["updated_at"] = time.Now()
	}

	sqlStr, params := m.query.Table(m.tableName).ResolveInsert(kv)

	res, err := m.db.ExecContext(ctx, sqlStr, params...)
	if err != nil {
		return 0, err
	}

	return res.LastInsertId()
}

// SaveAll save all debts to database
func (m *DebtModel) SaveAll(ctx context.Context, debts []DebtN) ([]int64, error) {
	ids := make([]int64, 0)
	for _, debt := range debts {
		id, err := m.Save(ctx, debt)
		if err != nil {
			return ids, err
		}

		ids = append(ids, id)
	}

	return ids, nil
}

// Save save a debt to database
func (m *DebtModel) Save(ctx context.Context, debt DebtN, onlyFields ...string) (int64, error) {
	return m.Create(ctx, debt.StaledKV(onlyFields...))
}

// SaveOrUpdate save a new debt or update it when it has a id > 0
func (m *DebtModel) SaveOrUpdate(ctx context.Context, debt DebtN, onlyFields ...string) (id int64, updated bool, err error) {
	if debt.Id.Int64 > 0 {
		_, _err := m.UpdateById(ctx, debt.Id.Int64, debt, onlyFields...)
		return debt.Id.Int64, true, _err
	}

	_id, _err := m.Save(ctx, debt, onlyFields...)
	return _id, false, _err
}

